package com.venkatesh;

public class doWhile_loop_20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		System.out.println("Printing the list of first 10 even number \n");
		
		do {
			System.out.println(i);
			i=i+2;
		}
		while(i<=10);
	}

}
